import React, { Component } from 'react'
import Search from './Search'
import Result from './Result'
import axios from 'axios'

export class Weather extends Component {
  constructor() {
    super()

    this.state = {
      city: "",
      lat: "",
      lon: "",
      weatherData: null
    }
  }

  inputChangeHandler = (e) => {
    console.log(e.target.name);
    const name = e.target.name

    if(name === "city"){
      this.setState({
        city: e.target.value
      })
    }else if(name === "lat"){
      this.setState({
        lat: e.target.value
      })
    }else if(name === "lon"){
      this.setState({
        long: e.target.value
      })
    }else{
      console.log("not valid");
    }
  }
  locationHandler = (e) =>{
    alert("kavita")
    e.preventDefault();

    if(navigator.geolocation){
      navigator.geolocation.getCurrentPosition((res) =>{
        setTimeout(()=>{
          this.setState({
            city:res.data.name,
            weatherData
          })
        })
      })
    }
  }

  render() {
    return (
      <div>
        <Search city={this.state.city} lat={this.state.lat} lon={this.state.lon} change={this.inputChangeHandler} location={this.locationHandler}></Search>
        <Result></Result>
      </div>
    )
  }
}

export default Weather
