import React, { Component } from 'react'
import Search from './Search'
import Result from './Result'
import axios from 'axios'
// import Loader from './Loader'

export class Weather extends Component {
  constructor() {
    super()

    this.state = {
      city: "",
      lat: "",
      lon: "",
      weatherData: null,
      loading:false,
      recent: []
    }
  }

  inputChangeHandler = (e) => {
    console.log(e.target.name);
    const name = e.target.name

    if(name === "city"){
      this.setState({
        city: e.target.value
      })
    }else if(name === "lat"){
      this.setState({
        lat: e.target.value
      })
    }else if(name === "lon"){
      this.setState({
        lon: e.target.value
      })
    }else{
      console.log("not valid");
    }
  }

  searchHandler =(e) => {
    e.preventDefault();
    // alert("fhg")
    axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${this.state.lat}&lon=${this.state.lon}&appid=c296989324d367f389749f3db62d7bff`).then((res) =>{
      console.log(res);
      this.setState({
        city:res.data.name,
        weatherData: res.data
      },()=>{
        this.recentHandler()
      })
    }).catch((err) => {
      console.log(err);
    })
  }


  locationHandler = (e) =>{
    // alert("kavita")
    e.preventDefault();
    this.setState({
      loading:true
    })

    if(navigator.geolocation){
      navigator.geolocation.getCurrentPosition((res) =>{
        setTimeout(()=>{
          this.setState({
            lat : res.coords.latitude,
            lon: res.coords.longitude
          })
          const { latitude, longitude} = res.coords
          axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=c296989324d367f389749f3db62d7bff`)
          .then((res) => {
            this.setState({
              city: res.data.name,
              weatherData: res.data,
              loading: false
        
            })
          }).catch((err) => {
            console.log(err);
          })
        },2000)
      },
      (err) =>{
        console.log(err);
      }
      )
    }else{
      console.log("Geolocation not Support...");
    }
  }

